import React, {useState} from "react";
import axios from "axios";
import { useHistory } from 'react-router-dom'; // импортируем useHistory для работы с маршрутизацией

export const Login = () => {
    const [phone, setPhone] = useState('');
    const [pass, setPass] = useState('');
    let history = useHistory(); // создаем объект history для маршрутизации

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Отправляем запрос на сервер для аутентификации
            const response = await axios.post('<api endpoint>/login', {
                phone: phone,
                password: pass
            });
            console.log(response.data);
            // Если аутентификация прошла успешно, перенаправляем пользователя на страницу Home
            history.push('/home');
        } catch (error) {
            console.error(error);
        }
    }

    return (
        <div className="auth-form-container">
            <form className="login-form" onSubmit={handleSubmit}>
                <label htmlFor="phone">Номер телефона</label>
                <input value={phone} onChange={(e) => setPhone(e.target.value)} type="phone" placeholder="Ваш номер телефона" id="phone" name="phone" />

                <label htmlFor="password">Пароль</label>
                <input value={pass} onChange={(e) => setPass(e.target.value)} type="password" placeholder="Ваш пароль" id="password" name="password" />
                <button type="submit">Войти</button>
            </form>
        </div>
    )
}
