import React, { useState, useEffect } from "react";
import axios from "axios";

export const Home = () => {
    const [customerInfo, setCustomerInfo] = useState({});
    const [accounts, setAccounts] = useState([]);
    const [name, setName] = useState([]);
    const [secondName, setSecondName] = useState([]);
    const [lastName, setLastName] = useState([]);
    const [phone, setPhone] = useState([]);
    const [timeDeposit, setTimeDeposit] = useState([]);
    const [demandDeposit, setDemandDeposit] = useState([]);
    const [cardDeposit, setCardDeposit] = useState([]);
    
    useEffect(() => {
        const fetchCustomerData = async () => {
            try {
                const customerResponse = await axios.get('https://localhost:7212/api/User');
                setCustomerInfo(customerResponse.phone);
                const accountsResponse = await axios.get('https://localhost:7212/api/User');
                setAccounts(accountsResponse.data);

            } catch (error) {
                console.error(error);
            }
        }
        fetchCustomerData();       
    }, []);

    return (
        <div className="customer-container">
            <h2>Добро пожаловать, {customerInfo.fullName}</h2>
            <p>Номер телефона: {customerInfo.phone}</p>

            <h3>Список счетов:</h3>
            <ul>
                {accounts.map((account, index) => (
                    <li key={index}>
                        {account.accountNumber} - {account.balance}
                    </li>
                ))}
            </ul>
        </div>
    )
}
